import React, { Component } from 'react'
import axios from 'axios';

class TransactionList extends Component {
    state = {transactions: []}

    render() {
        return (
            <div>
                <table>
                    <tbody>
                        {this.state.transactions.map((item, index) => {
                            return <tr key={index}>
                                <td>{item.firstName}</td>
                                <td>{item.lastName}</td>
                                <td>{item.city}</td>
                                <td>{item.totalPrice}</td>
                                <td>{item.currency}</td>
                                <td>{item.creditCardType}</td>
                                <td><button onClick={() => this.handleEdit(index)}>Edit</button></td>
                                <td><button onClick={() => this.handleDelete(index)}>Delete</button></td>
                            </tr>
                        })}
                    </tbody>
                </table>
            </div>
        )
    }

  componentDidMount() {
    axios.get('http://localhost:5000/transactions/')
      .then(response => {
        this.setState({ transactions: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  handleDelete(id) {
    console.log("adi")
    axios.delete('http://localhost:5000/transactions/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      transactions: this.state.transactions.filter(el => el._id !== id)
    })
  }

  handleEdit = (index) => {
    console.log("adi rosen")
    this.props.updateTransactionIndex(index)
  }

  //  handleDelete = (index) => {
  //   this.props.deleteTransaction(index)
  //  }
}

export default TransactionList
