import React, { Component } from 'react'

class InputForm extends Component {
	state = {
		firstName: '',
		lastName: '',
		email: '',
		gender: '',
		city: '',
    	street: '',
    	phone: '',
    	total_price: '',
    	currency: '',
    	credit_card_type: '',
    	credit_card_number: ''
	}

	render() {
		return (
			<form>
				<input name="firstName" placeholder="First Name" value={this.state.firstName} onChange={this.handleInputChange} />
				<input name="lastName" placeholder="Last Name" value={this.state.lastName} onChange={this.handleInputChange} />
				<input name="email" placeholder="Email" value={this.state.email} onChange={this.handleInputChange} />
				<input name="gender" placeholder="Gender" value={this.state.gender} onChange={this.handleInputChange} />
				<input name="city" placeholder="City" value={this.state.city} onChange={this.handleInputChange} />
				<input name="phone" placeholder="Phone Number" value={this.state.phone} onChange={this.handleInputChange} />
				<input name="total_price" placeholder="Total Price" value={this.state.total_price} onChange={this.handleInputChange} />

			</form>
		)
	}

	handleInputChange = e => {
		this.setState({
			[e.target.name] : e.target.value
		})
	}
}

export default InputForm
