import React, { Component } from 'react';
import axios from 'axios';

class InputForm extends Component {
	state = {
		firstName: '',
		lastName: '',
		email: '',
		gender: '',
		city: '',
    	street: '',
    	phone: '',
    	total_price: '',
    	currency: '',
    	credit_card_type: '',
    	credit_card_number: ''
	}

	render() {
		return (
			<form onSubmit= { this.handleOnSubmit }>
				<input name="firstName" placeholder="First Name" value={this.state.firstName} onChange={this.handleInputChange} /> <br/>
				<input name="lastName" placeholder="Last Name" value={this.state.lastName} onChange={this.handleInputChange} /> <br/>
				<input name="email" placeholder="Email" value={this.state.email} onChange={this.handleInputChange} /> <br/>
				<input name="gender" placeholder="Gender" value={this.state.gender} onChange={this.handleInputChange} /> <br/>
				<input name="city" placeholder="City" value={this.state.city} onChange={this.handleInputChange} /> <br/>
				<input name="phone" placeholder="Phone Number" value={this.state.phone} onChange={this.handleInputChange} /> <br/>
				<input name="total_price" placeholder="Total Price" value={this.state.total_price} onChange={this.handleInputChange} /> <br/>
				<input name="currency" placeholder="Currency" value={this.state.currency} onChange={this.handleInputChange} /> <br/>
				<input name="credit_card_type" placeholder="Credit Card Type" value={this.state.credit_card_type} onChange={this.handleInputChange} /> <br/>
				<input name="credit_card_number" placeholder="Credit Card Number" value={this.state.credit_card_number} onChange={this.handleInputChange} /> <br/>
				<button type="submit"> Submit Transaction </button>
			</form>
		)
	}

	handleInputChange = e => {
		console.log("heyyy")
		this.setState({
			[e.target.name] : e.target.value
		})
	}

	handleOnSubmit = e => {
		console.log("transaction");
		e.preventDefault();

		const transaction = {
			customer_id: 1,
			total_price: this.state.total_price,
			currency: this.state.currency,
			credit_card_type: this.state.credit_card_number,
			credit_card_number: this.state.credit_card_number
		}

		console.log("transaction" + transaction);
		axios.post('http://localhost:3000/transactions/add', transaction)
      		.then(res => console.log(res.data));

      		// reset state to blank
  //     	this.setState( {
		// 	firstName: '',
		// 	lastName: '',
		// 	email: '',
		// 	gender: '',
		// 	city: '',
	 //    	street: '',
	 //    	phone: '',
	 //    	total_price: '',
	 //    	currency: '',
	 //    	credit_card_type: '',
	 //    	credit_card_number: ''
		// })
		 // window.location = '/';
	}
}

export default InputForm
