import React, { Component } from 'react'
import axios from 'axios';
import TransactionForm from './TransactionForm';

class TransactionList extends Component {
    state = {transactions: []}

    render() {
        return (
            <div>
                <TransactionForm />
                <hr />
                <table>
                    <tbody>
                        {this.state.transactions.map((item, index) => {
                            return <tr key={index}>
                                <td>{item.firstName}</td>
                                <td>{item.lastName}</td>
                                <td>{item.city}</td>
                                <td>{item.totalPrice}</td>
                                <td>{item.currency}</td>
                                <td>{item.creditCardType}</td>
                                <td><button onClick={() => this.handleEdit(item._id)}>Edit</button></td>
                                <td><button onClick={() => this.handleDelete(item)}>Delete</button></td>
                            </tr>
                        })}
                    </tbody>
                </table>
            </div>
        )
    }

  componentDidMount() {
    axios.get('http://localhost:5000/transactions/')
      .then(response => {
        this.setState({ transactions: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  handleDelete = id =>  {
    axios.delete('http://localhost:5000/transactions/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      transactions: this.state.transactions.filter(el => el._id !== id)
    })
  }

  handleEdit = item => {
    console.log("adi rosen")
    this.props.handleEditTransaction(item);
  }
}

export default TransactionList
